import React, { use, useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { FaFolder, FaUser, FaArrowLeft,FaEye } from 'react-icons/fa';
import '../css/AlbumDetail.css';

const AlbumDetail = () => {
  const { albumId } = useParams();
  const navigate = useNavigate();
  const [album, setAlbum] = useState(null);

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/albums/${albumId}`)
      .then(res => res.json())
      .then(setAlbum);
  }
  , [albumId]);
  if (!album) return <div>Loading...</div>;

  return (
    <div className="main-layout">
      <aside className="sidebar">
        <div className="logo">
          <img
            src="https://agency.brvn.vn/u/geekup-logo_1682658002.jpg"
            alt="GeekUp Logo"
            style={{ width: 150, height: 150 }}
          />
        </div>
        <nav style={{width: 150}}>
          <ul>
            <li><Link to="/albums"><FaFolder /> Albums</Link></li>
            <li className="active"><FaUser /> Users</li>
          </ul>
        </nav>
      </aside>

      <main className="content">
        <div className="album-detail">
          <div className="breadcrumb">
            <button style={{backgroundColor:'white',color:'black',fontSize:"20px"}} onClick={() => navigate(-1)}>
              <FaFolder /> Albums / Show
            </button>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
            <button className='back-button' onClick={() => navigate(-1)}>
              <FaArrowLeft />
            </button>
            <h2 style={{ margin: 0 }}>Show Album</h2>
          </div>

          <div className="album-info" style={{ marginLeft: 24, display: 'flex', alignItems: 'center', gap: 12 }}>
            <img
              src={`https://ui-avatars.com/api/?name=${encodeURIComponent(album.title)}&background=ea4c89&color=fff&size=32`}
              alt={album.title}
              style={{ borderRadius: '50%' }}
            />
            <h3>{album.title}</h3>
          </div>

          {/* Add more album details here */}
        </div>
      </main>
    </div>
  );

};

export default AlbumDetail;
