import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { FaFolder, FaUser, FaArrowLeft, FaEye } from 'react-icons/fa';
import '../css/AlbumDetail.css';

const AlbumDetail = () => {
  const { albumId } = useParams();
  const navigate = useNavigate();
  const [album, setAlbum] = useState(null);

  useEffect(() => {
    // Giả sử fetch dữ liệu album theo albumId từ API
    fetch(`https://jsonplaceholder.typicode.com/albums/${albumId}`)
      .then(res => res.json())
      .then(data => {
        setAlbum(data);
      })
      .catch(err => {
        console.error("Lỗi khi lấy dữ liệu album:", err);
        // Nếu lỗi, có thể chuyển hướng về trang albums
        navigate('/albums');
      });
  }, [albumId, navigate]);

  if (!album) {
    return <div>Loading...</div>;
  }

  return (
    <div className="album-detail-container">
      <div className="header">
        <Link to="/albums" className="back-link">
          <FaArrowLeft /> Back to Albums
        </Link>
      </div>

      <div className="album-info">
        <div className="album-user">
          <div className="avatar">{album.userName?.slice(0, 2).toUpperCase()}</div>
          <div>
            <h3>{album.userName}</h3>
            <a href={`mailto:${album.userEmail}`}>{album.userEmail}</a>
          </div>
        </div>

        <h4>{album.title}</h4>
        <p>{album.description}</p>

        <div className="album-photos">
          {album.photos && album.photos.length > 0 ? (
            album.photos.map(photo => (
              <div key={photo.id} className="photo-card">
                <img src={photo.url} alt={photo.title} />
                <div className="photo-title">{photo.title}</div>
                <div className="photo-views">
                  <FaEye /> {photo.viewCount || 0}
                </div>
              </div>
            ))
          ) : (
            <p>No photos found in this album.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AlbumDetail;
