import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { FaFolder, FaUser, FaArrowLeft } from 'react-icons/fa';
import '../css/AlbumDetail.css';

const AlbumDetail = () => {
  const { albumId } = useParams();
  const navigate = useNavigate();
  const [album, setAlbum] = useState(null);
  const [user, setUser] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    // Fetch album, user, and photos
    fetch(`https://jsonplaceholder.typicode.com/albums/${albumId}`)
      .then(res => res.json())
      .then(albumData => {
        setAlbum(albumData);
        // Fetch user info
        fetch(`https://jsonplaceholder.typicode.com/users/${albumData.userId}`)
          .then(res => res.json())
          .then(setUser);
        // Fetch photos
        fetch(`https://jsonplaceholder.typicode.com/photos?albumId=${albumId}`)
          .then(res => res.json())
          .then(setPhotos)
          .finally(() => setLoading(false));
      })
      .catch(err => {
        setLoading(false);
        navigate('/albums');
      });
  }, [albumId, navigate]);

  if (loading || !album || !user) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="main-layout">
      <aside className="sidebar">
        <div className="logo">
          <img src="https://agency.brvn.vn/u/geekup-logo_1682658002.jpg" alt="GeekUp Logo" style={{ width: 150, height: 150 }} />
        </div>
        <nav>
          <ul>
            <li className="active"><Link to="/albums"><FaFolder /> Albums</Link></li>
            <li><Link to="/users"><FaUser /> Users</Link></li>
          </ul>
        </nav>
      </aside>
      <main className="content">
        <div className="user-detail">
          <div className="breadcrumb">
            <button className="back-button" onClick={() => navigate(-1)}>
              <FaArrowLeft /> Albums / Show
            </button>
          </div>
          <h2>Show Album</h2>
          <div className="user-info">
            <img
              src={`https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=ea4c89&color=fff&size=32`}
              alt={user.name}
              className="avatar"
            />
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <Link to={`/users/${user.id}`} className="user-link">{user.name}</Link>
              <a href={`mailto:${user.email}`}>{user.email}</a>
            </div>
          </div>
          <h3 style={{ marginTop: 24 }}>{album.title}</h3>
          <div className="album-photos" style={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
            {photos.length > 0 ? (
              photos.map(photo => (
                <div key={photo.id} style={{ width: 180, marginBottom: 16 }}>
                  <a href={photo.url.replace('https://via.placeholder.com', 'https://dummyjson.com/image')} target="_blank" rel="noopener noreferrer">
                    <img
                      src={photo.thumbnailUrl}
                      alt={photo.title}
                      style={{ width: '100%', borderRadius: 8, boxShadow: '0 1px 4px #0001', marginBottom: 6 }}
                      onError={e => {
                        e.target.onerror = null;
                        e.target.src = photo.url.replace('https://via.placeholder.com', 'https://dummyjson.com/image');
                      }}
                    />
                  </a>
                  <div style={{ fontSize: 13 }}>{photo.title}</div>
                </div>
              ))
            ) : (
              <p>No photos found in this album.</p>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default AlbumDetail;
