import React, { useEffect, useState } from 'react';
import { Link, useSearchParams, useNavigate } from 'react-router-dom';
import { FaFolder, FaUser } from 'react-icons/fa';
import '../css/UserList.css';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  //get data user
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users').then(res => res.json())
      .then(data => {
        setUsers(data);
        setLoading(false);
      });},[]);

  if (loading) { 
    return <div className="loading">Loading...</div>;
  }

    return(
      <div className='main-layout'>
        <aside className='sidebar'>
          <div className='logo'>
            <img src="https://agency.brvn.vn/u/geekup-logo_1682658002.jpg" alt="GeekUp Logo" style={{ width: 150, height: 150 }} />
          </div>
          <nav>
            <ul>
              <li><Link to="/albums"><FaFolder /> Albums</Link></li>
              <li className='active'><FaUser /> Users</li>
            </ul>
          </nav>
        </aside>

        <main className='content'>
          <div className='table-container'>
            <table className='user-table'>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Avatar</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Website</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id} onClick={() => navigate(`/users/${user.id}`)}>
                    <td>{user.id}</td>
                    <td>
                      <img
                        src={`https://ui-avatars.com/api/?background=random&rounded=true&name=${encodeURIComponent(user.name)}`}
                        alt={user.name}
                        style={{ width: 32, height: 32, borderRadius: '50%' }}
                      />
                    </td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.phone}</td>
                    <td>{user.website}</td>
                    <td>
                      <button className='view-button' onClick={() => navigate(`/users/${user.id}`)}>View</button>
                      </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </main>
      </div>
    )
  
};

export default UserList;
